import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";

export default function PostDetails() {
    const post_id = useParams().post_id

    const [post, setPost] = useState(null)
    const [comments, setComments] = useState([])

    useEffect(()=>{
        const fetchPost = async () => {
            try {
                const postResponse = await fetch(`http://localhost:9000/api/blog/posts/post/${post_id}`)
                const postData = await postResponse.json()

                const commentResponse = await fetch(`http://localhost:9000/api/blog/comments/post/${post_id}`)
                const commentData = await commentResponse.json()

                setPost(postData)
                setComments(commentData)
            } catch (err) {
                console.error('Error fetching post: ', err)
            }
        }

        fetchPost()
    },[])

    return(
        <div>
            <div style={{display: "flex"}}>
                <div>
                <NavLink to="/home">Home</NavLink>
                </div>
                <div>
                <NavLink to="/myposts">My Posts</NavLink>
                </div>
            </div>
            {post ? (
                <div>
                    <h2>{post.title}</h2>
                    <p>{post.content}</p>
                    <p><i>by <b>{post.username}</b></i></p>

                    <h3>Comments</h3>
                    {comments.length > 0 ? (
                        <ul>
                            {comments.map((comment) => (
                                comment.comment ? (
                                    <li key={comment.comment_id}>
                                        <p>{comment.comment}</p>
                                        <p><i>by <b>{comment.username}</b></i></p>
                                    </li>) : (null)
                            ))}
                        </ul>
                    ) : (
                        <div>
                            No comments available
                        </div>
                    )}
                </div>
            ) : (
                <div>
                    Hi
                </div>
            )}
        </div>
    )
}
