import {useState} from 'react';
import { useNavigate } from "react-router-dom";

export default function NewPost() {
    const [title, setTitle] = useState('')
    const [content, setContent] = useState('')
    const [message, setMessage] = useState(null)
    const username = localStorage.getItem('username')
    const navigate = useNavigate()

    const handleAddPost = async() => {
        const postData = {title: title, content: content, username: username}
        const response = await fetch('http://localhost:9000/api/blog/posts',{
            method: 'POST',
            body: postData,
            headers: {
                'Content-Type': 'application/json'
            }
        })

        const data = await response.json()

        if (response.ok) {
            const post_id = data.post_id

            navigate(`/post/${post_id}`)
        } else {
            setMessage(data.message)
        }
    }

    return(
        <div>
            <h2>Write a New Post...</h2>
            <input type='text' placeholder='Title' value={title} onChange={(e)=>setTitle(e.target.value)} />
            <br />
            <textarea rows='10' cols='80' placeholder="Content" value={content} onChange={(e)=>setContent(e.target.value)} />
            <button onClick={handleAddPost}>Add Post</button>
            <p>{message}</p>
        </div>
    )
}
