import {useEffect, useState} from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import LoginAndRegister from "./components/PAGE_loginandregister";
import AllPosts from "./components/PAGE_allposts";
import MyPostsPage from "./components/PAGE_myposts";
import PostDetails from "./components/PAGE_postdetails";
import checkTokenValidity from "./components/FUNCTION_checktokenvalidity";
import useAuth from "./components/FUNCTION_auth";

export default function App() {
  const [username, setUsername] = useState(null)

  useEffect(()=>{
    checkTokenValidity()
    useAuth()

    var user = localStorage.getItem('username')
    console.log
    setUsername(user)
  },[])

  return(
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element={<LoginAndRegister />} />
        <Route exact path="/home" element={<AllPosts />} />
        <Route exact path="/myposts" element={<MyPostsPage username={username} />} />
        <Route path="/post/:post_id" element={<PostDetails />} />
      </Routes>
    </BrowserRouter>
  )
}
