import {useEffect, useState} from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import LoginAndRegister from "./components/loginandregister";
import AllPosts from "./components/allposts";
import MyPostsPage from "./components/myposts";
import PostDetails from "./components/postdetails";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from 'jwt-decode'

export default function App() {
  const navigate = useNavigate()
  const [username, setUsername] = useState(null)

  useEffect(()=>{
    const token = localStorage.getItem('token')
    var user = localStorage.getItem('username')
    setUsername(user)

    if (token) {
      const decodedToken = jwtDecode(token)
      const expirationTime = decodedToken.exp * 1000

      if (Date.now() > expirationTime) {
        localStorage.removeItem('token')
      }
    }
  },[])

  return(
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element={<LoginAndRegister />} />
        <Route exact path="/home" element={<AllPosts />} />
        <Route exact path="/myposts" element={<MyPostsPage username={username} />} />
        <Route path="/post/:post_id" element={<PostDetails />} />
      </Routes>
    </BrowserRouter>
  )
}
