import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import NavBar from "./COMPONENT_navbar";
import useAuth from "./FUNCTION_auth";

export default function PostDetails() {
    useAuth()
    
    const post_id = useParams().post_id
    const currentuser = localStorage.getItem('username')

    const [post, setPost] = useState(null)
    const [comments, setComments] = useState([])
    const [newComment, setNewComment] = useState(null)
    const [message, setMessage] = useState(null)

    useEffect(()=>{
        const fetchPost = async () => {
            try {
                const postResponse = await fetch(`http://localhost:9000/api/blog/posts/post/${post_id}`)
                const postData = await postResponse.json()

                const commentResponse = await fetch(`http://localhost:9000/api/blog/comments/post/${post_id}`)
                const commentData = await commentResponse.json()

                setPost(postData)
                setComments(commentData)
            } catch (err) {
                console.error('Error fetching post: ', err)
            }
        }

        fetchPost()
    },[])

    const handleAddComment = async() => {
        const commentData = { post_id: post_id, username: currentuser, comment: newComment}

        const response_post = await fetch('http://localhost:9000/api/blog/comments', {
            method: 'POST',
            body: JSON.stringify(commentData),
            headers: { 'Content-Type' : 'application/json' }
        })

        var data_post = response_post.json()
        setMessage(data_post.message)
        
        cons
    }

    return(
        <div>
            <NavBar />
            {post ? (
                <div>
                    <h2>{post.title}</h2>
                    <p>{post.content}</p>
                    <p><i>by <b>{post.username}</b></i></p>

                    <h3>Comments</h3>
                    {comments.length > 0 ? (
                        <ul>
                            {comments.map((comment) => (
                                comment.comment ? (
                                    <li key={comment.comment_id}>
                                        <p>{comment.comment}</p>
                                        <p><i>by <b>{comment.username}</b></i></p>
                                    </li>) : (null)
                            ))}
                        </ul>
                    ) : (
                        <div>
                            No comments available
                        </div>
                    )}
                    <textarea rows='4' cols='75' placeholder="Add a comment..." value={newComment} />
                </div>
            ) : (
                <div>
                    Post unavailable
                </div>
            )}
        </div>
    )
}
