import { useState } from "react";
import { useNavigate } from 'react-router-dom'
import { jwtDecode } from 'jwt-decode'


export default function Login() {
    const navigate = useNavigate()
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [message, setMessage] = useState(null)
    const formData = {username: username, password: password}

    const handleSubmit = async (e) => {
        e.preventDefault()

        try {
            const response = await fetch('http://localhost:9000/api/blog/login', {
                method: 'POST',
                body: JSON.stringify(formData),
                headers: { 'Content-Type' : 'application/json' }
            })

            if (!response.ok) {
                const data = await response.json()
                setMessage(data.message)
            }

            var data = await response.json()
            const token = data.token
            
            const decodedToken = jwtDecode(token)
            localStorage.setItem('token',token)
            localStorage.setItem('username',decodedToken.username)

            navigate('/home')
        } catch (err) {
            console.error('Login failed: ', err)
        }
    }

    return (
        <>
        <form onSubmit={handleSubmit}>
            <label>
                Username: 
                <input type="text" name="username" value={formData.username} onChange={e=>setUsername(e.target.value)} />
            </label>
            <br />
            <label>
                Password: 
                <input type="password" name="password" value={formData.password} onChange={e=>setPassword(e.target.value)} />
            </label>
            <br />
            <button type="submit">Login</button>
        </form>
        <p>{message}</p>
        </>
    )
}
