import {useEffect, useState} from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import LoginAndRegister from "./components/loginandregister";
import AllPosts from "./components/allposts";
import MyPostsPage from "./components/myposts";
import PostDetails from "./components/postdetails";
import checkTokenValidity from "./components/checktokenvalidity";

export default function App() {
  const [username, setUsername] = useState(null)

  useEffect(()=>{
    checkTokenValidity
    var user = localStorage.getItem('username')
    setUsername(user)
  },[])

  return(
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element={<LoginAndRegister />} />
        <Route exact path="/home" element={<AllPosts />} />
        <Route exact path="/myposts" element={<MyPostsPage username={username} />} />
        <Route path="/post/:post_id" element={<PostDetails />} />
      </Routes>
    </BrowserRouter>
  )
}
