import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";

export default function PostDetailed() {
    const {post_id} = useParams()
    con
    const [post, setPost] = useState(null)
    const [comments, setComments] = useState([])

    useEffect(()=>{
        const fetchPost = async () => {
            try {
                const postResponse = await fetch(`http://localhost:9000/api/blog/posts/post/${post_id}`)
                const postData = postResponse.json()
                const commentResponse = await fetch(`http://localhost:9000/api/blog/comments/post/${post_id}`)
                const commentData = commentResponse.json()

                console.log(postData)
                console.log(commentData)

                setPost(postData)
                setComments(commentData)
            } catch (err) {
                console.error('Error fetching post: ', err)
            }
        }
    },[])

    return(
        <div>
            {post ? (
                <div>
                    <h2>{post.title}</h2>
                    <p>{post.content}</p>
                    <p><i>by <b>{post.username}</b></i></p>
                </div>
            ) : (
                <div>
                    Hi
                </div>
            )}
        </div>
    )
}
